if (Meteor.isClient) {

    Template.editUser.events({
        'click #editUserModal button[type="submit"]': function (event) {
            event.preventDefault();
        }
    });

    Template.editUser.helpers({
        email: function () {
            return getCurrentUser().profile.email;
        }
    });

    function getCurrentUser() {
        var template = Template.instance();
        template.data = Template.instance().data || {};
        template.data.profile = Accounts.users.findOne(
                Router.current().params.id
            ) || {};

        return template.data.profile;
    }
}
