Meteor.subscribe("userData");

if (Meteor.isClient) {

    // This code only runs on the client
    Template.userData.helpers({
        users: Accounts.users.find().fetch(),
        isCurrentUser: function (_id) {
            return _id === Meteor.userId();
        },
        onError: function () {
            return function (error) {
                alert("BOO!");
                console.log(error);
            };
        },
        onSuccess: function () {
            return function (result) {
                alert("YAY!");
                console.log(result);
            };
        },
        beforeRemove: function () {
            return function (collection, id) {
                var user = collection.findOne(id);
                if (confirm('Really remove user: ' + user.profile.name + '?')) {
                    this.remove();
                }
            };
        }
    });
}

Router.route('/users', function () {

    this.render('userData');

}, {name: 'users'});
