Meteor.subscribe("editUser");

if (Meteor.isClient) {

    editUserData = {};

    Template.editUser.helpers({

        updateUserData: function () {
            var user = getCurrentUser();
            editUserData.email = user.profile.email;
        }
    });

    function getCurrentUser() {
        return Accounts.users.findOne(
            $('#editUserModal').prop('data-user')
        );
    }
}
