Router.configure({
    // we use the  appBody template to define the layout for the entire app
    layoutTemplate: 'appBody',

    // the appNotFound template is used for unknown routes and missing lists
    notFoundTemplate: 'appNotFound',

    progress: true,

    // wait on the following subscriptions before rendering the page to ensure
    // the data it's expecting is present
    wait: function () {
        return [
            Meteor.subscribe('userData')
            //Meteor.subscribe('adminApp'),
            //Meteor.subscribe('publicApp'),
            //Meteor.subscribe('privateApp')
        ];
    }
});

dataReadyHold = null;

if (Meteor.isClient) {
    // Keep showing the launch screen on mobile devices until we have loaded
    // the app's data
    dataReadyHold = LaunchScreen.hold();
}
if (Meteor.isServer) {

    Router.onBeforeAction(function () {
        if (Meteor.isAdmin() && Router.current().url.match(/admin/)) {
            this.next();
        } else {
            this.redirect('/');
        }
    });

    Router.route('/', function () {
        this.render('Home');
        this.layout(null);
    });

    Router.route('/admin/users', function () {
        this.render('userData');
        Template.userData.__helpers.get('hideModal')();
    });

    Router.route('/admin/users/:id', function () {
        this.render('userData');
        Template.userData.__helpers.get('showModal')(this.params.id);
    });
}
