Accounts.onCreateUser(function (options, user) {
    debugger;

    var provider = Object.keys(user.services).shift() || '';
    var auth = getProviderInfo(user.services[provider]);

    if ((options || {}).profile) {
        options.profile.picture = auth.picture;
        options.profile.provider = provider;
        options.profile.email = auth.email;
        options.profile.link = auth.link;
        options.profile.createdAt = user.createdAt;
        user.profile = options.profile;
    }
    return user;
});

function getProviderInfo(provider) {

    var opts = {};

    switch (provider) {
        case 'facebook':
            opts = {
                email: provider.email,
                picture: 'http://graph.facebook.com/' + provider.id + '/picture/?type=small',
                link: provider.link
            };
            break;
        case 'github':
            opts = {
                email: provider.email,
                picture: '',
                link: ''
            };
            break;
        case 'google':
            opts = {
                email: provider.email,
                picture: '',
                link: ''
            };
            break;
        case 'twitter':
            opts = {
                email: provider.screenName,
                picture: '',
                link: ''
            };
            break;
    }

    return opts;
}
