Accounts.onCreateUser(function (options, user) {debugger;

    var provider = Object.keys(user).shift() || '';

    if ((options || {}).profile) {
        options.profile.picture = "http://graph.facebook.com/" + user.services.facebook.id + "/picture/?type=small";
        options.profile.email = user.services.facebook.email;
        options.profile.link = user.services.facebook.link;
        options.profile.createdAt = user.createdAt;
        user.profile = options.profile;
    }
    return user;
});

function getProviderInfo(provider) {

    var opts = {};

    switch(provider) {
        case 'facebook':
            opts = {
                email: provider.email,
                picture: 'http://graph.facebook.com/' + provider.id + '/picture/?type=small',
                link: provider.link
            };
            break;
    }

    return opts;
}
